document.addEventListener("DOMContentLoaded",()=>{const generateButton=document.getElementById("generate-lotto-button");const numbersContainer=document.getElementById("lotto-numbers-container");if(generateButton){generateButton.addEventListener("click",()=>{numbersContainer.innerHTML="";const candidateNumbers=Array.from({length:45},(_,i)=>i+1);const selectedNumbers=[];for(let i=0;i<6;i++){const randomIndex=Math.floor(Math.random()*candidateNumbers.length);selectedNumbers.push(candidateNumbers.splice(randomIndex,1)[0])}selectedNumbers.sort((a,b)=>a-b);selectedNumbers.forEach(number=>{const ball=document.createElement("div");ball.classList.add("lotto-ball");ball.textContent=number;if(number<=10)ball.style.backgroundColor="#f2b526";else if(number<=20)ball.style.backgroundColor="#3498db";else if(number<=30)ball.style.backgroundColor="#e74c3c";else if(number<=40)ball.style.backgroundColor="#2c3e50";else ball.style.backgroundColor="#2ecc71";numbersContainer.appendChild(ball)})})}const contactForm=document.getElementById("contact-form");const formStatus=document.getElementById("form-status");if(contactForm){contactForm.addEventListener("submit",async e=>{e.preventDefault();const form=e.target;const data=new FormData(form);try{const response=await fetch(form.action,{method:form.method,body:data,headers:{Accept:"application/json"}});if(response.ok){formStatus.textContent="메시지가 성공적으로 전송되었습니다. 감사합니다!";formStatus.style.color="green";form.reset()}else{const responseData=await response.json();if(Object.hasOwn(responseData,"errors")){formStatus.textContent=responseData["errors"].map(error=>error["message"]).join(", ")}else{formStatus.textContent="메시지 전송에 실패했습니다."}formStatus.style.color="red"}}catch(error){formStatus.textContent="메시지 전송 중 오류가 발생했습니다.";formStatus.style.color="red"}})}});